import React, { useRef } from "react";

export default function ScrollPage() {
    const scrollRef = useRef(null);

    const handleScroll = (direction) => {
      if (scrollRef.current) {
        // Width of a single image
        const imageWidth = 200; // Adjust this to match your image width
        const imageMargin = 16; // Adjust this to match the margin/padding between images
        const scrollAmount = 15 * (imageWidth + imageMargin); // Calculate total scroll distance
  
        scrollRef.current.scrollBy({
          left: direction === "right" ? scrollAmount : -scrollAmount,
          behavior: "smooth",
        });
      }
    };
  
    return (
      <div className="flex flex-col items-center space-y-4">
        <div
          ref={scrollRef}
          className="flex overflow-x-auto w-full max-w-4xl bg-gray-100 p-4 rounded-lg"
          style={{ scrollBehavior: "smooth" }}
        >
          {/* Add more images here */}
          {Array.from({ length: 50 }).map((_, index) => (
            <img
              key={index}
              src={`https://via.placeholder.com/200?text=Image+${index + 1}`}
              alt={`Image ${index + 1}`}
              className="mr-4 rounded-lg"
              style={{ width: "200px" }}
            />
          ))}
        </div>
        <div className="flex space-x-4">
          <button
            onClick={() => handleScroll("left")}
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
          >
            Scroll Left
          </button>
          <button
            onClick={() => handleScroll("right")}
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
          >
            Scroll Right
          </button>
        </div>
      </div>
    );
  }
